#!/usr/bin/env node
const { program } = require("commander");

program
  .name('global')
  .description('CLI to manage etranzact global application service.')
  .version('0.0.1-beta');

program.command('deploy', 'Deploy and rollback application builds.');
program.command('config', 'Manage application configurations and environment variables');
program.action(() => {
  program.help();
});

program.parse(process.argv);